mapper.insert:
INSERT INTO tb_task_run
(
  process_instance_id
, task_instance_id
, case_id
, task_status
, task_start_time
, task_suspend_time
, task_end_time
)
VALUES
    (
      #{processInstanceId}
    , #{taskInstanceId}
    , #{caseId}
    , #{taskStatus}
    , #{taskStartTime}
    , #{taskSuspendTime}
    , #{taskEndTime}
    )

mapper.update:
MERGE
    INTO tb_task_run A
        USING DUAL
        ON ( A.PROCESS_INSTANCE_ID  = 'process_instance_id'
            AND A.TASK_INSTANCE_ID  = 'task_instance_id'
            )
        WHEN MATCHED THEN
            UPDATE SET
                  A.task_status  = #{taskStatus}

                , A.task_start_time   = #{taskStartTime}


                , A.task_suspend_time = #{taskSuspendTime}


                , A.task_end_time   = #{taskEndTime}

        WHEN NOT MATCHED THEN
            INSERT
                (
                      process_instance_id
                    , task_instance_id
                    , case_id
                    , task_status
                    , task_start_time
                    , task_suspend_time
                    , task_end_time
                    )
                VALUES
                    (
                      #{processInstanceId}
                    , #{taskInstanceId}
                    , #{caseId}
                    , #{taskStatus}
                    , #{taskStartTime}
                    , #{taskSuspendTime}
                    , #{taskEndTime}
                    )

UPDATE tb_task_run
SET
    task_status       = #{taskStatus}

    , task_start_time   = #{taskStartTime}


    , task_suspend_time   = #{taskSuspendTime}


    , task_end_time   = #{taskEndTime}

WHERE 1=1
  AND process_instance_id = #{processInstanceId}
  AND task_instance_id = #{taskInstanceId}

mapper.insert:
INSERT INTO TB_PROCESS_INSTANCE
(
  PROCESS_INSTANCE_ID
, CASE_ID
, STATUS
, START_TIME
, SUSPEND_TIME
, END_TIME

)
VALUES
    (
      #{processInstanceId}
    , #{caseId}
    , #{status}
    , #{startTime}
    , #{suspendTime}
    , #{endTime}
    )

mapper.update:
UPDATE TB_PROCESS_INSTANCE
SET
status       = #{status}

    , start_time   = #{startTime}


    , suspend_time   = #{suspendTime}


    , end_time   = #{endTime}

WHERE 1=1
AND process_instance_id = #{processInstanceId}
